import numpy as np
import math
from typing import List

def orientation_comparison(contour1: List[List[int]], contour2: List[List[int]]) -> float:
    def calculate_angle(contour):
        vector = np.mean(contour, axis=0) - contour[0]
        angle = math.atan2(vector[1], vector[0])
        return math.degrees(angle)

    angle1 = calculate_angle(contour1)
    angle2 = calculate_angle(contour2)  # Calculate angle for contour2

    return abs(angle1 - angle2)
