from typing import List

def longest_common_substring(arr1: List[str], arr2: List[str]) -> str:
    def common_substr_in_all_arrays(substr):
        return all(substr in string for string in arr1 + arr2)

    def lcs(X, Y):
        m = len(X)
        n = len(Y)
        length = [[0] * (n + 1) for _ in range(m + 1)]
        result = 0
        end = 0

        for i in range(1, m + 1):
            for j in range(1, n + 1):
                if X[i - 1] == Y[j - 1]:
                    length[i][j] = length[i - 1][j - 1] + 1
                    if length[i][j] > result:
                        result = length[i][j]
                        end = i

        return X[end - result: end]

    common_substr = ''
    for string1 in arr1:
        for string2 in arr2:
            substr = lcs(string1, string2)
            if len(substr) > len(common_substr) and common_substr_in_all_arrays(substr):
                common_substr = substr

    return common_substr

